def minion_game(string1):
    input1 = list(string1)
    Stuart_list = []
    Kevin_list = []
    
    for i,j in zip(input1,range(0,len(input1))): 
        if i in consonants: 
            Stuart_list.append(len(input1) - j)
        else: 
            Kevin_list.append(len(input1) - j)

    if sum(Stuart_list) > sum(Kevin_list):
        print("Stuart " + str(sum(Stuart_list)))
    elif sum(Kevin_list) > sum(Stuart_list):
        print("Kevin " + str(sum(Kevin_list)))
    else: 
        print("Draw")
            
